package practice;

public class Largestamongthreenumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		int y = 7;
		int z = 6;
		
		if (x>y && x>z)
		{
			System.out.println("x is greatest");			
		}else if (y>z)
		{
			System.out.println("y is greatest");
		}
		else
		{
			System.out.println("z is greatest");
		}

	}

}
